import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faQuestionCircle as questionMark } from '@fortawesome/free-solid-svg-icons';
import sortItems from './../utils/sort';
import Pagination from './common/pagination';

const Admin = () => {
    const [coaches, setCoaches] = useState([]);
    const [coachName, setCoachName] = useState('');
    const [coachClassesCoached, setCoachClassesCoached] = useState([]);
    const [coachImage, setCoachImage] = useState('');
    const [coachFile, setCoachFile] = useState(undefined);
    const [coachOrder, setCoachOrder] = useState(999);
    const [showPopup, setShowPopup] = useState(false);
    const [itemToDelete, setItemToDelete] = useState(null);

    const formElement = useRef(null);

    const fetchCoaches = async () => {
        const coachList = await axios.get('/api/coaches');
        setCoaches(coachList.data);
    }

    const resetForm = () => {
        setCoachName('');
        setCoachClassesCoached([]);
        setCoachImage('');
        setCoachFile(undefined);
        formElement.current.reset();
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const newCoach = {
            name: coachName,
            classesCoached: coachClassesCoached,
            order: coachOrder,
            image: coachImage
        }
        const formData = new FormData();
        formData.append('imageData', coachFile[0]);
        const fileRes = await axios.post('/api/images', formData);
        const coachRes = await axios.post('/api/coaches', newCoach);
        console.log(fileRes.data);
        console.log(coachRes.data);
        if (fileRes.data === coachRes.data) {
            alert('SUCCESS!');
            const coachList = await axios.get('/api/coaches');
            setCoaches(coachList.data);
            resetForm();
        } else {
            alert('Something went wrong. Make sure image file type is .jpg or .png')
        }
    };

    const handleDelete = async (coach) => {
        console.log(coach);
        const coachRes = await axios.delete(`/api/coaches/${coach._id}`);
        const fileRes = await axios.delete(`/api/images/${coach.image}`);
        console.log(coachRes.data);
        console.log(fileRes.data);
        if (fileRes.data === coachRes.data) {
            alert('SUCCESS!');
            const coachList = await axios.get('/api/coaches');
            setCoaches(coachList.data);
        } else {
            alert('Something went wrong');
        }
    }

    const handleFileName = (fileName) => {
        const fileParts = fileName.split('\\');
        setCoachImage(fileParts[fileParts.length - 1])
    }

    const handleAddClass = (classToAdd) => {
        setCoachClassesCoached(coachClassesCoached => [...coachClassesCoached, classToAdd]);
    };

    useEffect(() => {
        fetchCoaches();
    }, [])

    return (<>
        <div className="container">
            <h1>ADMIN</h1>
            <h2>COACHES</h2>
            <div className={showPopup ? 'confirmDelete shown' : 'confirmDelete'}>
                <div>
                    <p>{`Are you sure you want to permanently delete ${itemToDelete ? itemToDelete.name : ''}?`}</p>
                    <span>
                        <button onClick={() => {
                            handleDelete(itemToDelete);
                            setItemToDelete(null);
                            setShowPopup(false);
                        }}>Yes</button>
                        <button onClick={() => { setShowPopup(false); setItemToDelete(null) }}>No</button>
                    </span>
                </div>
            </div>
            <table>
                <tbody>
                    {coaches.map(coach => {
                        return (
                            <tr key={coach._id}>
                                <td>{coach.name}</td>
                                <td>{coach.classesCoached}</td>
                                <td>{coach.order}</td>
                                <td><img className="thumbnailImg" src={`../../coachImages/${coach.image}`} alt={'picture of ' + coach.name} /></td>
                                <td><button onClick={() => { setItemToDelete(coach); setShowPopup(true) }}>Delete</button></td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
            <form ref={formElement} onSubmit={(e) => handleSubmit(e)}>
                <label htmlFor="name">Name</label>
                <input id="name" type="text" name="name" value={coachName} onChange={(e) => setCoachName(e.target.value)} required />
                <label htmlFor="classesCoached">Classes Coached</label>
                <select id="classesCoached" defaultValue={'-Select-'} name="classesCoached" onChange={(e) => {
                    handleAddClass(e.target.value);
                    e.target.value = '-Select-';
                }} required>
                    <option value="-Select-" disabled>-Select-</option>
                    <option value="Wrestling">Wrestling</option>
                    <option value="Jiu-Jitsu">Jiu-Jitsu</option>
                    <option value="Boxing">Boxing</option>
                    <option value="MMA">MMA</option>
                </select>
                {coachClassesCoached.length > 0
                    && coachClassesCoached.map(classCoached => {
                        console.log(classCoached);
                        return (
                            <div key={classCoached}>
                                <p>{classCoached}</p>
                                <button type="button" onClick={() => setCoachClassesCoached(coachClassesCoached.filter(x => x !== classCoached))}>Remove</button>
                            </div>
                        )
                    })}
                <label htmlFor="order">Display Order <FontAwesomeIcon className="icon" title='This controls the order in which coaches are displayed. "1" would be at the top of the page. Numbers can be repeated.' icon={questionMark} /></label>
                <input id="order" type="number" name="order" value={coachOrder} onChange={(e) => setCoachOrder(e.target.value)} required />
                <label htmlFor="image">Upload Image</label>
                <input type="file" id="image" name="image" onChange={(e) => { handleFileName(e.target.value); setCoachFile(e.target.files); }} required />
                <button type="submit">Submit</button>
                <button type="button" onClick={() => resetForm()}>Reset</button>
            </form>
        </div>
    </>);
}

export default Admin;
