import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Admin = () => {
    const [coaches, setCoaches] = useState([]);
    const [coachName, setCoachName] = useState('');
    const [coachClassesCoached, setCoachClassesCoached] = useState([]);
    const [coachImage, setCoachImage] = useState(null);

    const fetchCoaches = async () => {
        const coachList = await axios.get('/api/coaches');
        setCoaches(coachList.data);
    }

    const resetCoach = () => {
        setCoachName('');
        setCoachClassesCoached([]);
        setCoachImage(null);
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const res = await axios.post('/api/coaches', {
            name: coachName,
            classesCoached: coachClassesCoached,
            image: coachImage
        });
        console.log(res.data);
        resetCoach();
    };

    useEffect(() => {
        fetchCoaches();
    }, [])
    return (<>
        <div className="container">
            <h1>ADMIN</h1>
            <h2>COACHES</h2>
            <table>
                <tbody>
                    {coaches.map(coach => {
                        return (
                            <tr key={coach._id}>
                                <td>{coach.name}</td>
                                <td>{coach.classesCoached}</td>
                                <td>{coach.image}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
            <form onSubmit={(e) => handleSubmit(e)}>
                <label htmlFor="name">Name</label>
                <input id="name" type="text" name="name" value={coachName} onChange={(e) => setCoachName(e.target.value)} required />
                <label htmlFor="classesCoached">Classes Coached</label>
                <select id="classesCoached" type="select" name="classesCoached" value={coachClassesCoached} onChange={(e) => setCoachClassesCoached([e.target.value])} required>
                    <option value="none" defaultValue disabled>-Select-</option>
                    <option value="Wrestling">Wrestling</option>
                    <option value="Boxing">Boxing</option>
                    <option value="MMA">MMA</option>
                </select>
                <label htmlFor="image">Upload Image</label>
                <input type="file" id="image" name="image" value={coachImage} onChange={(e) => setCoachImage(e.target.value)} required />
                <button type="submit">Submit</button>
            </form>
        </div>
    </>);
}

export default Admin;
