import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Admin = () => {
    const [coaches, setCoaches] = useState([]);
    const [coach, setCoach] = useState({
        name: '',
        classesCoached: '',
        image: ''
    });

    const fetchCoaches = async () => {
        const coachList = await axios.get('/api/coaches');
        setCoaches(coachList.data);
    }

    const resetCoach = () => {
        setCoach({
            name: '',
            classesCoached: '',
            image: ''
        });
    }

    const handleChange = async (e) => {
        let coachLocal = coach;
        coachLocal[e.target.name] = e.target.value;
        setCoach(coachLocal);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const res = await axios.post('/api/coaches');
        setCoaches(res.data);
        resetCoach();
    };

    useEffect(() => {
        fetchCoaches();
    }, [])
    return (<>
        <div className="container">
            <h1>ADMIN</h1>
            <h2>COACHES</h2>
            <table>
                <tbody>
                    {coaches.map(coach => {
                        return (
                            <tr key={coach._id}>
                                <td>{coach.name}</td>
                                <td>{coach.classesCoached}</td>
                                <td>{coach.image}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
            <form onSubmit={(e) => handleSubmit(e)}>
                <label htmlFor="name">Name</label>
                <input id="name" name="name" value={coach.name} onChange={(e) => handleChange(e)} required />
                <label htmlFor="classesCoached">Classes Coached</label>
                <input id="classesCoached" name="classesCoached" value={coach.classesCoached} onChange={(e) => handleChange(e)} required />
                <label htmlFor="image">Upload Image</label>
                <input type="file" id="image" name="image" value={coach.image} onChange={(e) => handleChange(e)} required />
                <button type="submit">Submit</button>
            </form>
        </div>
    </>);
}

export default Admin;
