import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Admin = () => {
    const [coaches, setCoaches] = useState([]);
    const [coachName, setCoachName] = useState('');
    const [coachClassesCoached, setCoachClassesCoached] = useState([]);
    const [coachImage, setCoachImage] = useState('');
    const [coachFile, setCoachFile] = useState(undefined);

    const fetchCoaches = async () => {
        const coachList = await axios.get('/api/coaches');
        setCoaches(coachList.data);
    }

    const resetCoach = () => {
        setCoachName('');
        setCoachClassesCoached([]);
        setCoachImage('');
        setCoachFile(undefined);
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const fileRes = await axios.post('/api/files', coachFile);
        const coachRes = await axios.post('/api/coaches', {
            name: coachName,
            classesCoached: coachClassesCoached,
            image: coachImage
        });
        console.log(fileRes.data);
        console.log(coachRes.data);
        fileRes.data === coachRes.data && alert('SUCCESS!');
        resetCoach();
    };

    const handleFileUpload = (fileName) => {
        const fileParts = fileName.split('\\');
        setCoachImage(fileParts[fileParts.length - 1])
    }

    const handleAddClass = (classToAdd) => {
        setCoachClassesCoached(coachClassesCoached => [...coachClassesCoached, classToAdd]);
    };

    // const handleRemoveClass = (classCoached) => {
    //     console.log(classCoached);
    //     const newClasses = coachClassesCoached.splice(coachClassesCoached.indexOf(classCoached), 1);
    //     setCoachClassesCoached(newClasses);
    // }

    useEffect(() => {
        fetchCoaches();
    }, [])
    return (<>
        <div className="container">
            <h1>ADMIN</h1>
            <h2>COACHES</h2>
            <table>
                <tbody>
                    {coaches.map(coach => {
                        return (
                            <tr key={coach._id}>
                                <td>{coach.name}</td>
                                <td>{coach.classesCoached}</td>
                                <td>{coach.image}</td>
                            </tr>
                        )
                    })}
                </tbody>
            </table>
            <form onSubmit={(e) => handleSubmit(e)}>
                <label htmlFor="name">Name</label>
                <input id="name" type="text" name="name" value={coachName} onChange={(e) => setCoachName(e.target.value)} required />
                <label htmlFor="classesCoached">Classes Coached</label>
                <select id="classesCoached" defaultValue={'-Select-'} name="classesCoached" onChange={(e) => handleAddClass(e.target.value)} required>
                    <option value="-Select-" disabled>-Select-</option>
                    <option value="Wrestling">Wrestling</option>
                    <option value="Boxing">Boxing</option>
                    <option value="MMA">MMA</option>
                </select>
                {coachClassesCoached.length > 0
                    && coachClassesCoached.map(classCoached => {
                        console.log(classCoached);
                        return (
                            <div key={classCoached}>
                                <p>{classCoached}</p>
                                <button type="button" onClick={() => setCoachClassesCoached(coachClassesCoached.filter(x => x !== classCoached))}>Remove</button>
                            </div>
                        )
                    })}
                <label htmlFor="image">Upload Image</label>
                <input type="file" id="image" name="image" value={coachFile} onChange={(e) => { handleFileUpload(e.target.value); setCoachFile(e.target.value) }} required />
                <button type="submit">Submit</button>
            </form>
        </div>
    </>);
}

export default Admin;
