import React, { useState } from 'react';

const TableBody = ({ data, onDelete, onUpdate }) => {
    const [showPopup, setShowPopup] = useState(false)
    const [itemToDelete, setItemToDelete] = useState(null);

    return (
        <>
            <tbody>
                <div className={showPopup ? 'confirmDelete shown' : 'confirmDelete'}>
                    <div>
                        <p>{`Are you sure you want to permanently delete ${itemToDelete ? itemToDelete.name : ''}?`}</p>
                        <span>
                            <button onClick={() => {
                                onDelete(itemToDelete._id);
                                setItemToDelete(null);
                                setShowPopup(false);
                            }}>Yes</button>
                            <button onClick={() => setShowPopup(false)}>No</button>
                        </span>
                    </div>
                </div>
                {data.map(item => {
                    return (
                        <tr key={item._id}>
                            <td onClick={() => { onUpdate(item) }}><p className="updateLink" title="Click to update item">{item.name}</p></td>
                            {item.classesCoached && <td>{item.classesCoached}</td>}
                            {item.order && <td>{item.order}</td>}
                            {item.image && <td><img className="thumbnailImg" src={`../../coachImages/${item.image}`} alt={'picture of ' + item.name} /></td>}
                            <td>
                                <button onClick={() => {
                                    setItemToDelete(item);
                                    setShowPopup(true)
                                }}>Delete</button>
                            </td>
                        </tr>
                    )
                })}
            </tbody>
            <tbody>
                {data.map((item) => {
                    const date = item.date ? new Date(item.date).toDateString() : null;
                    return (
                        <tr key={item._id}>
                            <td onClick={() => { onUpdate(item) }}><p className="updateLink" title="Click to update item">{item.name}</p></td>
                            {date ? <td>{date}</td> : null}
                            {item.location ? <td>{item.location}</td> : null}
                            {item.link ? <td>{item.link}</td> : null}
                            {item.experienceLevel ? <td>{item.experienceLevel}</td> : null}
                            {item.backToCenter ? <td>{item.backToCenter}</td> : null}
                            {item.finishOnLeg ? <td>{item.finishOnLeg}</td> : null}
                            {item.defendLegs ? <td>{item.defendLegs}</td> : null}
                            {item.frontHeadOffense ? <td>{item.frontHeadOffense}</td> : null}
                            {item.frontHeadDefense ? <td>{item.frontHeadDefense}</td> : null}
                            {item.attackRate ? <td>{item.attackRate}</td> : null}
                            {item.finishOnTop ? <td>{item.finishOnTop}</td> : null}
                            {item.last30Score ? <td>{item.last30Score}</td> : null}
                            <td><button onClick={() => {
                                setItemToDelete(item);
                                setShowPopup(true)
                            }}>Delete</button></td>
                        </tr>
                    )
                })}
            </tbody>
        </>
    );
}

export default TableBody;
